package elvis.leetcode;

public class CharacterReplacement {
    public int characterReplacement(String s, int k) {
        int left = 0, right = 0;
        int[] m = new int[26];
        int max = 0;
        while (right < s.length()) {
            m[s.charAt(right) - 'A']++;
            max = Math.max(max, m[s.charAt(right) - 'A']);
            if (max + k < right - left + 1) {
                m[s.charAt(left) - 'A']--;
                left++;
            }
            right++;
        }
        return right - left;
    }

    public static void main(String[] args) {
        CharacterReplacement cr = new CharacterReplacement();
        long t0 = System.currentTimeMillis();
//        System.out.println(cr.characterReplacement("AABABBA", 1));
        System.out.println(cr.characterReplacement("BAABA", 1));
//        System.out.println(cr.characterReplacement("", 100));
        System.out.println(System.currentTimeMillis() - t0 + "ms");
    }
}
