package elvis.leetcode;

public class FindSubstringInWraproundString {
    public int findSubstringInWraproundString(String p) {
        int n = p.length();
        if (n == 0)
            return 0;
        int[] dp = new int[n];
        dp[0] = 1;
        for (int i = 1; i < n; i++) {
            dp[i] = dp[i - 1];
            //第i个字符之前出现过
            if (p.substring(0, i).indexOf(p.charAt(i)) == -1)
                dp[i]++;
            for (int j = i - 1; j >= 0; j--) {
                //字符不是有序的, 链条断裂, 再往前不用算
                if (p.charAt(j + 1) - p.charAt(j) != 1 && (p.charAt(j + 1) != 'a' || p.charAt(j) != 'z'))
                    break;
                //判断字符串是否在之前出现过
                if (j == 0 || !p.substring(0, i).contains(p.substring(j, i + 1)))
                    dp[i]++;
            }
        }
        return dp[n - 1];
    }

    public static void main(String[] args) {
        FindSubstringInWraproundString f = new FindSubstringInWraproundString();
        long t0 = System.currentTimeMillis();
//        System.out.println(f.findSubstringInWraproundString("zab"));
//        System.out.println(f.findSubstringInWraproundString("cac"));
//        System.out.println(f.findSubstringInWraproundString("zabc"));
//        System.out.println(f.findSubstringInWraproundString("abab"));
//        System.out.println(f.findSubstringInWraproundString(""));
        System.out.println(f.findSubstringInWraproundString(""));
        System.out.println(System.currentTimeMillis() - t0);
    }
}
